import React, { useState } from "react";
import { Row, Col, Card, Typography,Button,Input } from "antd";
const { Title } = Typography;

function App() {
  const [input,setinput]= useState("");

  const timefun=()=>{

    setInterval(()=>{
    const data = new Date();
    settime(data.toLocaleTimeString());
    var x= new Date().toLocaleTimeString("en-us",{timeZone:"Europe\/Paris"});
    console.log(x)
  });

}
const gettime = () =>{
  fetch("http://worldtimeapi.org/api/timezone")
}

timefun()
  return (
    <div className="App">
    <Row justify="center">
      <Col span={24}>
        <Card style={{ height: 100 }}>
          <Title level={1}>Weather App</Title>
        </Card>
      </Col>
    </Row>
    <br />
    <br />
    <Row justify="center">

    </Row>
    <Form>
      <Row justify="center" style={{ marginTop: 80 }}>
        <Col span={6}>
          <Input placeholder="Enter the city name......" size="large" onChange={(e)=>setinput(e.target.value)}/>
        </Col>
      </Row>
      <br />
      <br />
      <Row justify="center">
        <Col span={2}>
          <Button type="primary"  onClick={gettime}>
            Search
          </Button>
          </Col>
          <Col span={1}>
          <Button type="primary"  >
            reset
          </Button>
        </Col>
      </Row>
    </Form>
  </div>
  );
}

export default App;
