import React, { useState } from "react";
import { Row, Col, Card, Typography, Button, Input, Form } from "antd";
const { Title } = Typography;

function App() {
  const [input, setinput] = useState("");
  const [time, settime] = useState("");
  const [valuee, setvaluee] = useState("");
  const [totalvaluee, settotalvaluee] = useState("");
  const getdata = async () => {
    const europeres = await fetch(
      `http://worldtimeapi.org/api/timezone/europe`
    );
    const europedata = await europeres.json();
    const americares = await fetch(
      `http://worldtimeapi.org/api/timezone/america`
    );
    const americadata = await americares.json();
    const africares = await fetch(
      `http://worldtimeapi.org/api/timezone/africa`
    );
    const africadata = await africares.json();
    const asiares = await fetch(`http://worldtimeapi.org/api/timezone/asia`);
    const asiadata = await asiares.json();
    const australiares = await fetch(
      `http://worldtimeapi.org/api/timezone/australia`
    );
    const australiadata = await australiares.json();
    const antarcticares = await fetch(
      `http://worldtimeapi.org/api/timezone/antarctica`
    );
    const antarcticadata = await antarcticares.json();
    const totaldata = europedata.concat(
      americadata,
      africadata,
      asiadata,
      australiadata,
      antarcticadata
    );

    settotalvaluee(totaldata);

  };

  let value;
  const timefun = () => {
    setInterval(() => {
      const data = new Date();
      settime(data.toLocaleTimeString());
      value = new Date().toLocaleTimeString("en-us", {
        timeZone: "Australia/Darwin",
      });
      setvaluee(value);
    });
  };
  // const gettime = async () => {
  //    const res= await fetch(`http://worldtimeapi.org/api/timezone/${input}`);
  //    const data= await res.json();
  //    console.log(data);
  // };

  timefun();
  return (
    <div className="App">
      <Row justify="center">
        <Col span={24}>
          <Card style={{ height: 100 }}>
            <Title level={1}>Weather App</Title>
          </Card>
        </Col>
      </Row>
      <br />
      <br />
      <Row justify="center">
        {time}
        {valuee}
      </Row>
      <Form>
        <Row justify="center" style={{ marginTop: 80 }}>
          <Col span={6}>
            <Input
              placeholder="Enter the city name......"
              size="large"
              onChange={(e) => setinput(e.target.value)}
            />
          </Col>
        </Row>
        <br />
        <br />
        <Row justify="center">
          <Col span={2}>
            <Button type="primary" onClick={getdata()}>
              Search
            </Button>
          </Col>
          <Col span={1}>
            <Button type="primary">reset</Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
}

export default App;
