import React, { useState } from "react";
import { Row, Col, Card, Typography, Button, Input, Form } from "antd";
const { Title } = Typography;

function App() {
  const [input, setinput] = useState("");
  const [livelocationtime, setlivelocationtime] = useState("");
  const [time, settime] = useState("");




  let value;

  const timefun = () => {
    setInterval(() => {
      const data = new Date();
      setlivelocationtime(data.toLocaleTimeString());

    });
  };
  const gettime = async () => {
    let apikey = "238fc375db844255858e5c3680125079";
     const res= await fetch(`https://api.ipgeolocation.io/timezone?apiKey=${apikey}&location=${input}`);
     const data= await res.json(); 
     const timezone = data.timezone;
     console.log()
     setInterval(()=>{
      value = new Date().toLocaleTimeString("en-us", {
        timeZone:`${timezone}`,
     });


    });
    settime(value);

  };

  timefun();
  return (
    <div className="App">
      <Row justify="center">
        <Col span={24}>
          <Card style={{ height: 100 }}>
            <Title level={1}>Time</Title>
          </Card>
        </Col>
      </Row>
      <br />
      <br />
      <Row justify="center">
        {livelocationtime}
        {time}
      </Row>
      <Form>
        <Row justify="center" style={{ marginTop: 80 }}>
          <Col span={6}>
            <Input
              placeholder="Enter the city name......"
              size="large"
              onChange={(e) => setinput(e.target.value)}
            />
          </Col>
        </Row>
        <br />
        <br />
        <Row justify="center">
          <Col span={2}>
            <Button type="primary" onClick={gettime} >
              Search
            </Button>
          </Col>
          <Col span={1}>
            <Button type="primary">reset</Button>
          </Col>
        </Row>
      </Form>
    </div>
  );
}

export default App;
